function getWeather(e){fetch(`https://api.openweathermap.org/data/2.5/weather?q=${e}&units=metric&APPID=5d066958a60d315387d9492393935c19`).then((e=>e.json())).then((e=>{const t=document.getElementById("temperature"),n=document.getElementById("pressure"),d=document.getElementById("description"),o=document.getElementById("humidity"),m=document.getElementById("wind-speed"),i=document.getElementById("wind-direction"),c=document.getElementById("weather-icon");t.textContent=e.main.temp,n.textContent=e.main.pressure,d.textContent=e.weather[0].description,o.textContent=e.main.humidity,m.textContent=e.wind.speed,i.textContent=e.wind.deg,c.src="https://openweathermap.org/img/w/10d.png"})).catch((e=>{console.log("Помилка при отриманні даних про погоду",e)}))}getWeather("LVIV");